import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AlertController, LoadingController } from '@ionic/angular';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.page.html',
  styleUrls: ['./signup.page.scss'],
})
export class SignupPage implements OnInit {
  formData: { [key: string]: string } = {
    displayName: '',
    email: '',
    password: '',
    confirmPassword: ''
  };
  errors: any = {};

  constructor(
    private router: Router,
    private alertController: AlertController,
    private loadingController: LoadingController
  ) { }

  ngOnInit() {
  }

  updateFormData(field: string, value: string) {
    this.formData[field] = value;
    if (this.errors[field]) {
      this.errors[field] = '';
    }
  }

  validateForm() {
    this.errors = {};

    if (!this.formData.displayName.trim()) {
      this.errors.displayName = 'Display name is required';
    } else if (this.formData.displayName.trim().length < 2) {
      this.errors.displayName = 'Display name must be at least 2 characters';
    }

    if (!this.formData.email.trim()) {
      this.errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(this.formData.email)) {
      this.errors.email = 'Please enter a valid email';
    }

    if (!this.formData.password.trim()) {
      this.errors.password = 'Password is required';
    } else if (this.formData.password.length < 6) {
      this.errors.password = 'Password must be at least 6 characters';
    }

    if (!this.formData.confirmPassword.trim()) {
      this.errors.confirmPassword = 'Please confirm your password';
    } else if (this.formData.password !== this.formData.confirmPassword) {
      this.errors.confirmPassword = 'Passwords do not match';
    }

    return Object.keys(this.errors).length === 0;
  }

  async handleSignup() {
    if (!this.validateForm()) return;

    const loading = await this.loadingController.create({
      message: 'Creating your account...',
    });
    await loading.present();

    try {
      // TODO: Implement Firebase authentication
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      await loading.dismiss();
      
      const alert = await this.alertController.create({
        header: 'Account Created!',
        message: 'Your account has been created successfully.',
        buttons: [
          {
            text: 'OK',
            handler: () => {
              this.router.navigate(['/room-selection']);
            }
          }
        ]
      });
      await alert.present();
    } catch (error) {
      await loading.dismiss();
      const alert = await this.alertController.create({
        header: 'Error',
        message: 'Failed to create account. Please try again.',
        buttons: ['OK']
      });
      await alert.present();
    }
  }

  goToLogin() {
    this.router.navigate(['/login']);
  }

}
<ion-header>
  <ion-toolbar>
    <ion-title>Create Account</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content class="signup-content">
  <div class="signup-container">
    <!-- Header -->
    <div class="header">
      <h1 class="title">Create Account</h1>
      <p class="subtitle">Join us and start your love story</p>
    </div>

    <!-- Form -->
    <div class="form">
      <ion-item class="custom-input" [class.error]="errors.displayName">
        <ion-label position="stacked">Display Name</ion-label>
        <ion-input 
          type="text" 
          [(ngModel)]="formData.displayName" 
          placeholder="How should we call you?"
          (ionInput)="updateFormData('displayName', $event.detail.value || '')">
        </ion-input>
      </ion-item>
      <div class="error-message" *ngIf="errors.displayName">{{ errors.displayName }}</div>

      <ion-item class="custom-input" [class.error]="errors.email">
        <ion-label position="stacked">Email</ion-label>
        <ion-input 
          type="email" 
          [(ngModel)]="formData.email" 
          placeholder="Enter your email"
          (ionInput)="updateFormData('email', $event.detail.value || '')">
        </ion-input>
      </ion-item>
      <div class="error-message" *ngIf="errors.email">{{ errors.email }}</div>

      <ion-item class="custom-input" [class.error]="errors.password">
        <ion-label position="stacked">Password</ion-label>
        <ion-input 
          type="password" 
          [(ngModel)]="formData.password" 
          placeholder="Create a password"
          (ionInput)="updateFormData('password', $event.detail.value)">
        </ion-input>
      </ion-item>
      <div class="error-message" *ngIf="errors.password">{{ errors.password }}</div>

      <ion-item class="custom-input" [class.error]="errors.confirmPassword">
        <ion-label position="stacked">Confirm Password</ion-label>
        <ion-input 
          type="password" 
          [(ngModel)]="formData.confirmPassword" 
          placeholder="Confirm your password"
          (ionInput)="updateFormData('confirmPassword', $event.detail.value)">
        </ion-input>
      </ion-item>
      <div class="error-message" *ngIf="errors.confirmPassword">{{ errors.confirmPassword }}</div>

      <ion-button 
        expand="block" 
        class="custom-button custom-button-primary"
        (click)="handleSignup()">
        Create Account
      </ion-button>
    </div>

    <!-- Terms -->
    <div class="terms">
      <p class="terms-text">
        By creating an account, you agree to our 
        <a href="#" class="terms-link">Terms of Service</a> and 
        <a href="#" class="terms-link">Privacy Policy</a>
      </p>
    </div>

    <!-- Footer -->
    <div class="footer">
      <p class="footer-text">Already have an account? 
        <ion-button fill="clear" (click)="goToLogin()">Sign In</ion-button>
      </p>
    </div>
  </div>
</ion-content>
