import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AlertController, LoadingController } from '@ionic/angular';

@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})
export class LoginPage implements OnInit {
  email: string = '';
  password: string = '';
  errors: any = {};

  constructor(
    private router: Router,
    private alertController: AlertController,
    private loadingController: LoadingController
  ) { }

  ngOnInit() {
  }

  validateForm() {
    this.errors = {};

    if (!this.email.trim()) {
      this.errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(this.email)) {
      this.errors.email = 'Please enter a valid email';
    }

    if (!this.password.trim()) {
      this.errors.password = 'Password is required';
    } else if (this.password.length < 6) {
      this.errors.password = 'Password must be at least 6 characters';
    }

    return Object.keys(this.errors).length === 0;
  }

  async handleLogin() {
    if (!this.validateForm()) return;

    const loading = await this.loadingController.create({
      message: 'Signing you in...',
    });
    await loading.present();

    try {
      // TODO: Implement Firebase authentication
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      await loading.dismiss();
      this.router.navigate(['/room-selection']);
    } catch (error) {
      await loading.dismiss();
      const alert = await this.alertController.create({
        header: 'Error',
        message: 'Failed to sign in. Please try again.',
        buttons: ['OK']
      });
      await alert.present();
    }
  }

  async handleForgotPassword() {
    const alert = await this.alertController.create({
      header: 'Forgot Password',
      message: 'Password reset functionality will be implemented with Firebase.',
      buttons: ['OK']
    });
    await alert.present();
  }

  goToSignup() {
    this.router.navigate(['/signup']);
  }

}
<ion-header>
  <ion-toolbar>
    <ion-title>Sign In</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content class="login-content">
  <div class="login-container">
    <!-- Header -->
    <div class="header">
      <h1 class="title">Welcome Back</h1>
      <p class="subtitle">Sign in to continue your love story</p>
    </div>

    <!-- Form -->
    <div class="form">
      <ion-item class="custom-input" [class.error]="errors.email">
        <ion-label position="stacked">Email</ion-label>
        <ion-input 
          type="email" 
          [(ngModel)]="email" 
          placeholder="Enter your email"
          (ionInput)="errors.email = ''">
        </ion-input>
      </ion-item>
      <div class="error-message" *ngIf="errors.email">{{ errors.email }}</div>

      <ion-item class="custom-input" [class.error]="errors.password">
        <ion-label position="stacked">Password</ion-label>
        <ion-input 
          type="password" 
          [(ngModel)]="password" 
          placeholder="Enter your password"
          (ionInput)="errors.password = ''">
        </ion-input>
      </ion-item>
      <div class="error-message" *ngIf="errors.password">{{ errors.password }}</div>

      <ion-button 
        expand="block" 
        fill="clear" 
        class="forgot-password"
        (click)="handleForgotPassword()">
        Forgot Password?
      </ion-button>

      <ion-button 
        expand="block" 
        class="custom-button custom-button-primary"
        (click)="handleLogin()">
        Sign In
      </ion-button>
    </div>

    <!-- Footer -->
    <div class="footer">
      <p class="footer-text">Don't have an account? 
        <ion-button fill="clear" (click)="goToSignup()">Sign Up</ion-button>
      </p>
    </div>
  </div>
</ion-content>
